# Load .Net Assemblies for using PogU WinForms
Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing

# Variables
$GameRoot = (Get-ChildItem HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall | % { Get-ItemProperty $_.PsPath } | where { $_.displayname -eq "ARK: Survival Evolved" }).InstallLocation
$PackageInfo = "$GameRoot\PackageInfo.bin"
$INIPath = "$GameRoot\Engine\Config\ConsoleVariables.ini"
$INIBack = "ConsoleVariables.ini.backup"
$TexturePath = "$GameRoot\ShooterGame\Content\PrimalEarth\Effects\Textures\Generic\"
$WaterAsset = "SoftEdgeGradient_Linear.uasset"
$WaterAssetPath = $TexturePath + $WaterAsset
$WaterAssetBackup = "SoftEdgeGradient_Linear.uasset.backup"
$WaterAssetBackupPath = $TexturePath + $WaterAssetBackup

# List of INIs
# Name,BASE64 of ini file's contents
$INIList = @"
Name,Base64
Default,
Stalkopat's very cool transparent water ini,
Pretty ini,
Rev ini,
Rev ini - no trees,
Insanely hard ini,
Recommended alternative ini for scouting,
"@ | ConvertFrom-Csv


# Need to make a check that $GameRoot has data
# Test if PackageInfo exists (I assume to see if game is actually installed)
Switch ( Test-Path $PackageInfo -PathType leaf ) {
    $false {[System.Windows.Forms.MessageBox]::Show("$PackageInfo doesnt exist","Error",0);exit}
}

# Set up INI Selector form
$Form = New-Object System.Windows.Forms.Form
$Form.Text = "ARK client toolkit"
$Form.StartPosition = "CenterScreen"
$Form.FormBorderStyle = 'FixedDialog'
 
# Create a group that will contain your radio buttons
$MyGroupBox = New-Object System.Windows.Forms.GroupBox
$MyGroupBox.Location = '40,30'
$MyGroupBox.text = "Select ini to use"


# Dynamically create Radio Buttons
$RadioLocation = New-Object System.Drawing.Point
$RadioLocation.X = 20
$RadioLocation.Y = 40
ForEach ($ini in $INIList) {
    $RadioButton1 = New-Object System.Windows.Forms.RadioButton
    $RadioButton1.Location = $RadioLocation
    $RadioButton1.size = '350,20'
    $RadioButton1.Checked = $false
    $RadioButton1.Text = $ini.name
    $MyGroupBox.Controls.AddRange($Radiobutton1)
    $RadioLocation.Y = $RadioLocation.Y + 30
}

# Create checkbox
$CheckboxLocation = New-Object System.Drawing.Point
$CheckboxLocation.X = 60
$CheckboxLocation.Y = $RadioLocation.Y + 50
$checkbox1 = new-object System.Windows.Forms.checkbox
$checkbox1.Location = $CheckboxLocation
$checkbox1.Size = '350,20'
$checkbox1.Text = "Disable Water Texture (Will try to reenable if unchecked)"
$checkbox1.Checked = $true
$Form.Controls.AddRange($checkbox1)  

# Dynamically set groupbox size based on $INIList
$GroupBoxSize = New-Object System.Drawing.Size
$GroupBoxSize.Width = 400
$GroupBoxSize.Height = $RadioLocation.Y
$MyGroupBox.size = $GroupBoxSize

# Dynamically set Form size based on $INIList
$FormSize = New-Object System.Drawing.Size
$FormSize.Width = 500
$FormSize.Height = $RadioLocation.Y + 180
$Form.Size = $FormSize

# Add an OK button
$OKButtonLocation = New-Object System.Drawing.Point
$OKButtonLocation.X = 130
$OKButtonLocation.Y = $Form.Height - 80

$OKButton = new-object System.Windows.Forms.Button
$OKButton.Location = $OKButtonLocation
$OKButton.Size = '100,40' 
$OKButton.Text = 'OK'
$OKButton.DialogResult=[System.Windows.Forms.DialogResult]::OK
 
#Add a cancel button
$CancelButtonLocation = New-Object System.Drawing.Point
$CancelButtonLocation.X = 255
$CancelButtonLocation.Y = $Form.Height - 80

$CancelButton = new-object System.Windows.Forms.Button
$CancelButton.Location = $CancelButtonLocation
$CancelButton.Size = '100,40'
$CancelButton.Text = "Cancel"
$CancelButton.DialogResult=[System.Windows.Forms.DialogResult]::Cancel
 
# Add all the Form controls on one line 
$form.Controls.AddRange(@($MyGroupBox,$OKButton,$CancelButton))

# Assign the Accept and Cancel options in the form to the corresponding buttons
$form.AcceptButton = $OKButton
$form.CancelButton = $CancelButton
 
# Activate the form
$form.Add_Shown({$form.Activate()})    
    
# Get the results from the button click
$dialogResult = $form.ShowDialog()

# Test if user clicked OK or Cancel
Switch ( $dialogResult.value__ ) {
    2 { Exit $LASTEXITCODE }
}

# Compare selected radial with list of INIs
$INIValue = $INIList | Where { $_.name -eq $(($Form.Controls.controls | where {$_.checked -eq $true}).Text )}

# Decode Base64 of ini and output it to $INIPath
# Also a fail safe if somehow $INIValue doesnt return a value and would wipe the existing ini
If ( $INIValue ) {
    [System.Text.Encoding]::ASCII.GetString([System.Convert]::FromBase64String($INIValue.Base64)) | Out-File $INIPath
}

# If it's stupid and it works...
If ( $checkbox1.Text | Where { $_ -eq $(($Form.Controls | where {$_.checked -eq $true}).Text )} ) {
    If ( Test-Path $WaterAssetPath ) {
        Rename-Item -Path $WaterAssetPath -NewName $WaterAssetBackup
    }
} Else {
    Rename-Item -Path $WaterAssetBackupPath -NewName $WaterAsset
}